responses.body:
{
  "entries": [
    {{# repeat 10 }}
      {
        "notificationId": "{{ concat 'ntf' (faker 'datatype.number' min=10000 max=100000) }}",
        "user": {
          "userId": "{{ concat 'u' (faker 'datatype.number' min=10000 max=100000) }}",
          "firstname": "{{ faker 'name.firstName' }}",
          "lastname": "{{ faker 'name.lastName' }}",
          "avatar": "{{ faker 'image.avatar' }}"
        },
        "seen": {{ faker 'datatype.boolean' }},
        "reaction": {
          "reactionId": "{{ concat 'r' (faker 'datatype.number' min=10000 max=100000) }}",
          "reaction": "{{ oneOf (array 'up' 'down' 'heart' 'frown') }}",
          "createdAt": "{{ faker 'date.recent' }}",
          "givenBy": {
            "userId": "{{ concat 'u' (faker 'datatype.number' min=10000 max=100000) }}",
            "firstname": "{{ faker 'name.firstName' }}",
            "lastname": "{{ faker 'name.lastName' }}",
            "avatar": "{{ faker 'image.avatar' }}"
          },
          "post": {
            "postId": "{{ concat 'pst' (faker 'datatype.number' min=10000 max=100000) }}",
            "contentShort": "{{ faker 'lorem.sentence' }}",
            "thumbnail": "https://loremflickr.com/640/480/cats",
            "channel": {
              "channelId": "{{ concat 'ch' (faker 'datatype.number' min=10000 max=100000) }}",
              "name": "{{ faker 'lorem.words' }}"
            },
            "author": {
              "userId": "{{ concat 'u' (faker 'datatype.number' min=10000 max=100000) }}",
              "firstname": "{{ faker 'name.firstName' }}",
              "lastname": "{{ faker 'name.lastName' }}",
              "avatar": "{{ faker 'image.avatar' }}"
            }
          }
        }
      },
    {{/ repeat }}
  ],
  "total": 10
}

